import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Button, TextField, Grid, Card, CardContent, CardActions, Dialog, DialogTitle, DialogContent, DialogActions, CircularProgress, Snackbar, Alert } from '@mui/material';
import axios from '../../api/axios';

const ManageBuses = () => {
  const [buses, setBuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [open, setOpen] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [currentBus, setCurrentBus] = useState(null);
  const [alertMessage, setAlertMessage] = useState({ message: '', severity: 'success' });
  const [alertOpen, setAlertOpen] = useState(false);
  
  const [formData, setFormData] = useState({
    busNo: '',
    image: '',
    busSeat: 0
  });

  useEffect(() => {
    fetchBuses();
  }, []);

  const fetchBuses = async () => {
    try {
      const response = await axios.get('/Bus');
      setBuses(response.data);
    } catch (error) {
      console.error('Error fetching buses:', error);
      showAlert('Failed to load buses', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'busSeat' ? parseInt(value, 10) : value
    });
  };

  const handleOpen = (bus = null) => {
    if (bus) {
      setEditMode(true);
      setCurrentBus(bus);
      setFormData({
        busNo: bus.busNo,
        image: bus.image || '',
        busSeat: bus.busSeat
      });
    } else {
      setEditMode(false);
      setCurrentBus(null);
      setFormData({
        busNo: '',
        image: '',
        busSeat: 0
      });
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const showAlert = (message, severity) => {
    setAlertMessage({ message, severity });
    setAlertOpen(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (editMode) {
        await axios.put(`/Bus/${currentBus._id}`, formData);
        showAlert('Bus updated successfully', 'success');
      } else {
        await axios.post('/Bus', formData);
        showAlert('Bus added successfully', 'success');
      }
      
      fetchBuses();
      handleClose();
    } catch (error) {
      console.error('Error saving bus:', error);
      showAlert(editMode ? 'Failed to update bus' : 'Failed to add bus', 'error');
    }
  };

  const handleDelete = async (busId) => {
    if (window.confirm('Are you sure you want to delete this bus?')) {
      try {
        await axios.delete(`/Bus/${busId}`);
        showAlert('Bus deleted successfully', 'success');
        fetchBuses();
      } catch (error) {
        console.error('Error deleting bus:', error);
        showAlert('Failed to delete bus', 'error');
      }
    }
  };

  if (loading) {
    return (
      <Container sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress />
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Manage Buses
      </Typography>
      
      <Button 
        variant="contained" 
        color="primary" 
        onClick={() => handleOpen()}
        sx={{ mb: 3 }}
      >
        Add New Bus
      </Button>
      
      <Grid container spacing={3}>
        {buses.map((bus) => (
          <Grid item xs={12} sm={6} md={4} key={bus._id}>
            <Card>
              <CardContent>
                <Typography variant="h6" component="div">
                  Bus No: {bus.busNo}
                </Typography>
                <Typography color="text.secondary">
                  Total Seats: {bus.busSeat}
                </Typography>
                {bus.image && (
                  <img 
                    src={bus.image} 
                    alt={`Bus ${bus.busNo}`} 
                    style={{ maxWidth: '100%', height: 'auto', marginTop: '10px' }}
                  />
                )}
              </CardContent>
              <CardActions>
                <Button size="small" onClick={() => handleOpen(bus)}>Edit</Button>
                <Button size="small" color="error" onClick={() => handleDelete(bus._id)}>Delete</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      
      {/* Add/Edit Bus Dialog */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{editMode ? 'Edit Bus' : 'Add New Bus'}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="busNo"
            label="Bus Number"
            type="text"
            fullWidth
            value={formData.busNo}
            onChange={handleChange}
            required
          />
          <TextField
            margin="dense"
            name="image"
            label="Image URL"
            type="text"
            fullWidth
            value={formData.image}
            onChange={handleChange}
          />
          <TextField
            margin="dense"
            name="busSeat"
            label="Number of Seats"
            type="number"
            fullWidth
            value={formData.busSeat}
            onChange={handleChange}
            required
            InputProps={{ inputProps: { min: 1 } }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>{editMode ? 'Update' : 'Add'}</Button>
        </DialogActions>
      </Dialog>
      
      <Snackbar 
        open={alertOpen} 
        autoHideDuration={6000} 
        onClose={() => setAlertOpen(false)}
      >
        <Alert 
          onClose={() => setAlertOpen(false)} 
          severity={alertMessage.severity}
        >
          {alertMessage.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default ManageBuses;
