import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Grid, TextField, Button, FormControl, InputLabel, Select, MenuItem, Snackbar, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from '../../api/axios';
import { useAuth } from '../../hooks/useAuth';

const BookTicket = () => {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [buses, setBuses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const [formData, setFormData] = useState({
    busID: '',
    routeID: 'RT001', // Default route ID
    date: '',
    time: '',
    seats: 1,
    price: 0,
    status: 'pending',
    passengerID: currentUser?._id || ''
  });

  useEffect(() => {
    const fetchBuses = async () => {
      try {
        const response = await axios.get('/Bus');
        setBuses(response.data);
      } catch (error) {
        console.error('Error fetching buses:', error);
        setErrorMessage('Failed to load available buses');
      } finally {
        setLoading(false);
      }
    };

    fetchBuses();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Calculate price based on seats (example calculation)
      const calculatedPrice = formData.seats * 500; // 500 per seat
      
      const bookingData = {
        ...formData,
        price: calculatedPrice,
        bookingID: `BK${Date.now().toString().slice(-6)}`,
        passengerID: currentUser?._id
      };
      
      const response = await axios.post('/Booking', bookingData);
      setSuccessMessage('Ticket booked successfully!');
      
      // Redirect to bookings page after short delay
      setTimeout(() => {
        navigate('/customer/bookings');
      }, 2000);
    } catch (error) {
      console.error('Error booking ticket:', error);
      setErrorMessage('Failed to book ticket. Please try again.');
    }
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Book Your Ticket
        </Typography>
        
        <form onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Select Bus</InputLabel>
                <Select
                  name="busID"
                  value={formData.busID}
                  onChange={handleChange}
                  required
                  label="Select Bus"
                >
                  {loading ? (
                    <MenuItem disabled>Loading buses...</MenuItem>
                  ) : (
                    buses.map(bus => (
                      <MenuItem key={bus._id} value={bus._id}>
                        {bus.busNo} - Seats: {bus.busSeat}
                      </MenuItem>
                    ))
                  )}
                </Select>
              </FormControl>
            </Grid>
            
            <Grid item xs={12} sm={6}>
              <TextField
                label="Journey Date"
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
                required
                fullWidth
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            
            <Grid item xs={12} sm={6}>
              <TextField
                label="Journey Time"
                type="time"
                name="time"
                value={formData.time}
                onChange={handleChange}
                required
                fullWidth
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            
            <Grid item xs={12} sm={6}>
              <TextField
                label="Number of Seats"
                type="number"
                name="seats"
                value={formData.seats}
                onChange={handleChange}
                required
                fullWidth
                InputProps={{ inputProps: { min: 1, max: 10 } }}
              />
            </Grid>
            
            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                fullWidth
                size="large"
                disabled={loading}
              >
                Book Ticket
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
      
      <Snackbar open={!!successMessage} autoHideDuration={6000} onClose={() => setSuccessMessage('')}>
        <Alert severity="success" onClose={() => setSuccessMessage('')}>
          {successMessage}
        </Alert>
      </Snackbar>
      
      <Snackbar open={!!errorMessage} autoHideDuration={6000} onClose={() => setErrorMessage('')}>
        <Alert severity="error" onClose={() => setErrorMessage('')}>
          {errorMessage}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default BookTicket;
